        />
        <Text style={styles.Titulo}>Datos testigos</Text>
        <Text style={styles.subTitulo}>Ingrese nombre de testigo 1*</Text>
        <TextInput
          value={nombreTestigo1}
          onChangeText={setNombreTestigo1}
