import React, { useState } from 'react';
import { View, Text, Picker } from 'react-native'; // Importa Picker de react-native
import dataEstablecimientos from './dataEstablecimientos'; // Importa el arreglo de datos

const EstablecimientosScreen = () => {
  const [selectedEstablecimiento, setSelectedEstablecimiento] = useState(null); // Estado para almacenar el establecimiento seleccionado

  const handleEstablecimientoChange = (value) => {
    const establecimiento = dataEstablecimientos.find((est) => est.idComuna === value); // Encuentra el establecimiento en el arreglo de datos basado en el valor seleccionado del Picker
    setSelectedEstablecimiento(establecimiento); // Actualiza el estado con el establecimiento seleccionado
  };

  return (
    <View>
       {dataEstablecimientos.map((establecimiento, index) => (
        <View key={index}>
          <Text>ID Comuna: {establecimiento.idComuna}</Text>
          <Text>ID Tipo: {establecimiento.idTipo}</Text>
          <Text>Nombre: {establecimiento.nombre}</Text>
          <Text>------------------------------</Text>
        </View>
      ))}
    </View>
  );
};

export default EstablecimientosScreen;
