import React from 'react';
import { useState } from 'react';
import { StyleSheet, Text, View, TextInput, Image, StatusBar, Button, Alert, ScrollView } from 'react-native';
import { Picker } from '@react-native-picker/picker';
import { useNavigation } from '@react-navigation/native';

/* import SQLite from 'react-native-sqlite-storage';
import db from './Utils/DataBase' */
import COLORS from './../../consts/colors';
import { PrimaryButton } from '../components/Button';

const Perfil = () => {
  const [imageUri, setImageUri] = useState(null);
  const [nombre, setNombre] = useState('');
  const [comuna, setComuna] = useState('');
  const [tipoEstablecimiento, setTipoEstablecimiento] = useState('');
  const [establecimiento, setEstablecimiento] = useState('');

  const pickImage = async () => {
    await getGalleryPermission();
    const result = await ImagePicker.launchImageLibraryAsync();
    if (!result.canceled) {
      setImageUri(result.uri); // Usamos result.assets[0].uri en lugar de result.uri
    }
  };

  const getGalleryPermission = async () => { // Actualizamos la función getGalleryPermission
    const { status } = await ImagePicker.requestMediaLibraryPermissionsAsync();
    if (status !== 'granted') {
      alert('Se requiere permiso para acceder a la galería de imágenes');
    }
  };

  const savePerfil = async () => {
    if (!nombre || !comuna || !establecimiento) {
      Alert.alert('Error', 'Por favor complete todos los campos obligatorios');
    } else {
      Alert.alert('Guardado exitoso');
    }
  }
  return (

    <View style={styles.container}>
      <ScrollView>
      <StatusBar backgroundColor="black" />

      <Image
        style={styles.imageLogo}
        source={require('../assets/logo_MS.png')}
      />

      {imageUri ? (
        <Image source={{ uri: imageUri }} style={styles.imagePerfil} />
      ) : (
        <View>
          <Image
            source={require('../assets/avatar-default.jpg')}
            style={styles.imagePerfil}
          />
        </View>
      )}

      <View style={styles.botonImagen}>
        <Button title="Seleccionar imagen" onPress={pickImage} />
      </View>
      <Text style={styles.Titulo}>Ingrese nombre</Text>
      <TextInput
        value={nombre}
        placeholder="Nombre completo"
        onChangeText={setNombre}
        maxLength={10}
        style={styles.textInput}
      />



      <Text style={styles.Titulo}>Seleccione comuna</Text>
      <View style={styles.multiSelect}>
        <Picker
          selectedValue={comuna}
          onValueChange={(itemValue) => setComuna(itemValue)}
        >
          <Picker.Item label="Seleccione" value="" />
        </Picker>
      </View>

      <Text style={styles.Titulo}>Seleccione tipo establecimiento</Text>
      <View style={styles.multiSelect}>
        <Picker
          selectedValue={tipoEstablecimiento}
          onValueChange={(itemValue) => setTipoEstablecimiento(itemValue)}
        >
          <Picker.Item label="Seleccione" value="" />
        </Picker>
      </View>

      <Text style={styles.Titulo}>Seleccione establecimiento</Text>
      <View style={styles.multiSelect}>
        <Picker
          selectedValue={establecimiento}
          onValueChange={(itemValue) => setEstablecimiento(itemValue)}
        >
          <Picker.Item label="Seleccione" value="" />
        </Picker>
      </View>




      <View style={styles.Buttons}>
        <PrimaryButton
          title="Guardar"
          color="blue"
          //onPress={() => guardarPerfil()}/>
          onPress={() => navigation.navigate("Perfil")} />

      </View>
    </ScrollView>
        </View >
  )
      }
//------------------------------------------------------------------------------------------
const styles = StyleSheet.create({

  container: {
    paddingVertical: 20,
    paddingHorizontal: 30,

    /* alignItems: 'center', */
  },

  Titulo: {
    marginTop: 15,
    textAlign: 'center',
    fontWeight: 'bold',
    fontSize: 14,
  },

  subTitulo: {
    flex: 1,
    marginTop: 15,
    height: 35,
    borderRadius: 10,
    flexDirection: 'row',
    paddingHorizontal: 20,
  },

  textInput: {
    flex: 1,
    height: 50,
    fontSize: 13,
    borderRadius: 10,
    flexDirection: 'row',
    backgroundColor: COLORS.white,
    alignItems: 'center',
    paddingHorizontal: 20,
  },
  buttonLayout: {
    justifyContent: 'center',
    alignItems: 'flex-start',
    marginTop: 10
  },
  Buttons: {
    fontSize: 13,
    height: 50,
    marginTop: 15,
  },

  multiSelect: {
    fontSize: 13,
    marginLeft: 10,
    borderRadius: 10,
    backgroundColor: COLORS.white,

    /* justifyContent: 'center',
    alignItems: 'center', */
  },
  /* container: {
    flex: 1,
    backgroundColor: '#1a9bd7',
  },
  textInput:{
    alignSelf:'center',
    padding: 10,
    paddingStart:30,
    width:'80%',
    height: 50,
    marginTop:20,
    borderRadius:30,
    backgroundColor:'#fff'
  },
    subTitulo:{
      fontSize:20,
      color:"white",
      alignSelf:'center',
      paddingTop:10
      
    },
  buttonLayout: {
    justifyContent: 'center',
    alignItems: 'flex-start', 
    marginTop:10 
  },
  multiSelect:{
    padding: 10,
    paddingStart:20,
    backgroundColor:'#fff',
    borderRadius: 30,
    width:'80%',
    height: 50,
    alignSelf:'center',
    
  },
  buttons: {
    bottom: 0,
    left: 0,
    right: 0,
    marginTop:20,
    flexDirection: 'row',
    justifyContent: 'space-evenly',
    paddingVertical: 20,
    paddingHorizontal: 10,
    backgroundColor: '#1a9bd7',
  }, */

});

export default Perfil
