import React, { useState, useEffect } from 'react';
import { View, Text, Picker } from 'react-native';

import dataComunaEstablecimiento from '../../consts/dataComunaEstablecimiento'
import dataEstablecimientos from '../../consts/dataEstablecimientos'
import dataTipoEstablecimiento from '../../consts/dataTipoEstablecimiento'

const EstablecimientosPicker = () => {
    const [comunaId, setComunaId] = useState('');
    const [tipoId, setTipoId] = useState('');
    const [establecimientos, setEstablecimientos] = useState([]);


    const [comunaSeleccionada, setComunaSeleccionada] = useState('');
    const [tipoSeleccionado, setTipoSeleccionado] = useState('');
    const [establecimientoSeleccionado, setEstablecimientoSeleccionado] = useState('');


    const filtrarEstablecimientos = () => {
        const establecimientosFiltrados = dataEstablecimientos.filter(establecimiento => {
            return establecimiento.idComuna === comunaSeleccionada && establecimiento.idTipo === tipoSeleccionado;
        });
        return establecimientosFiltrados.map(establecimiento => {
            return <Picker.Item key={establecimiento.nombre} label={establecimiento.nombre} value={establecimiento.nombre} />
        });
    };

    return (
        <View style={styles.container}>
            <Text style={styles.title}>Selecciona una comuna:</Text>
            <Picker
                selectedValue={comunaSeleccionada}
                onValueChange={(itemValue) => setComunaSeleccionada(itemValue)}
            >
                {dataComunaEstablecimiento.map((comuna) => (
                    <Picker.Item key={comuna.id} label={comuna.name} value={comuna.id} />
                ))}
            </Picker>

            <Text style={styles.title}>Selecciona un tipo de establecimiento:</Text>
            <Picker
                selectedValue={tipoSeleccionado}
                onValueChange={(itemValue) => setTipoSeleccionado(itemValue)}
            >
                {dataTipoEstablecimiento.map((tipo) => (
                    <Picker.Item key={tipo.id} label={tipo.nombre} value={tipo.id} />
                ))}
            </Picker>

            {comunaSeleccionada && tipoSeleccionado &&
                <>
                    <Text style={styles.title}>Selecciona un establecimiento:</Text>
                    <Picker
                        selectedValue={establecimientoSeleccionado}
                        onValueChange={(itemValue) => setEstablecimientoSeleccionado(itemValue)}
                    >
                        {filtrarEstablecimientos()}
                    </Picker>
                </>
            }
        </View>
    );
};
const styles = StyleSheet.create({
    container: {
      flex: 1,
      alignItems: 'center',
      justifyContent: 'center',
      backgroundColor: '#F5FCFF',
    },
    title: {
      fontSize: 20,
      fontWeight: 'bold',
      marginTop: 20,
    },
  });

export default EstablecimientosPicker;
